import React from 'react';
import Board from './board';
import Snake from './snake';
import Food  from './food';
import './game.css';

class Game extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			running: false,
			height: 0,
			width:  0,

			food:  [],
			snake: [],
			dir: null
		};
	}

	componentDidMount() {
		// event handler for keypress event
		window.onkeypress = this.handleKeyPress;
		redrawFood();
	}

	handleKeyPress = (event) => {
		// press spacebar to start
		if (event.key === " ") {

			this.setState({
				running: true
			});

		} else if (["w", "a", "s", "d"].includes(event.key)) {

			this.setState({
				dir: event.key
			});
		}
	}

	setDimensions = (height, width) => {
		this.setState({
			height: height,
			width:  width
		});
	}

	// 
	typeOf = ([x, y]) => {
		// 
		const food = this.state.food;
		if (x === food[0] && y === food[1]) {
			return "food";
		}

		//
		const snake = this.state.snake;
		for (let i = 0; i < snake.length; i++) {
			if (x === snake[i][0] && y === snake[i][1]) {
				return "snake";
			}
		}

		// else
		return "";
	}

	redrawSnake() {

	}

	redrawFood() {
		let x = Math.floor(Math.random() * this.state.col);
		let y = Math.floor(Math.random() * this.state.row);

		//

		this.setState({
			food: [x, y]
		});
	}

	//
	checkCollision() {

	}

	render() {
		return (
			<div>
				<div className="game-title">{"Snake"}</div>
				<div className="game-board">
					<Board
						setDimensions={this.setDimensions}
						typeOf={this.typeOf}
					/>
				</div>

				<Food
					coords={this.state.food}
					avoid={this.state.snake}
					update={this.redrawFood}
				/>
				<Snake
					coords={this.state.snake}
					dir={this.state.dir}
					update={this.redrawSnake}
				/>
			</div>
		);
	}
}

export default Game;
