import React from 'react';
import Board from './board';
import './game.css';

class Game extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			running: false,
			height: 20,
			width:  30,

			food:  [],
			snake: [],
			dir: ""
		};
	}

	componentDidMount() {
		// event handler for keypress event
		window.onkeypress = this.handleKeyPress;

		this.initFood();
		this.initSnake();

		// initialize timer
		this.interval = setInterval(() => {
			// redraw if game is running
			if (this.state.running) {
				this.redrawFood();
				this.redrawSnake();
			}
		}, 100);
	}

	// clear timer when component unmounts
  	componentWillUnmount() {
    	clearInterval(this.interval);
  	}

	handleKeyPress = (event) => {
		// press spacebar to start
		if (event.key === " ") {
			this.setState({
				running: true
			});

		} else if (["w", "a", "s", "d"].includes(event.key)) {

			this.setState({
				dir: event.key
			});
		}
	}

	// 
	typeOf = ([x, y]) => {
		// 
		const food = this.state.food;
		if (x === food[0] && y === food[1]) {
			return "food";
		}

		//
		const snake = this.state.snake;
		for (let i = 0; i < snake.length; i++) {
			if (x === snake[i][0] && y === snake[i][1]) {
				return "snake";
			}
		}

		// else
		return "";
	}

	initSnake() {
		const x = this.state.width  / 2;
		const y = this.state.height / 2;

		let snake = [];
		for (let i = 0; i < 5; i++) {
			snake.push([x + i, y]);
		}

		this.setState({
			snake: snake
		});		
	}

	redrawSnake() {

	}

	initFood() {
		let x = Math.floor(Math.random() * this.state.width);
		let y = Math.floor(Math.random() * this.state.height);

		//


		this.setState({
			food: [x, y]
		});
	}

	redrawFood() {

	}

	//
	checkCollision() {

	}

	render() {
		return (
			<div>
				<div className="game-title">{"Snake"}</div>
				<div className="game-board">
					<Board
						row={this.state.height}
						col={this.state.width}
						typeOf={this.typeOf}
					/>
				</div>
			</div>
		);
	}
}

export default Game;
